document.addEventListener("DOMContentLoaded",function(){const prefersReducedMotion=window.matchMedia("(prefers-reduced-motion: reduce)").matches;if(prefersReducedMotion){return}const cursor=document.createElement("div");cursor.className="custom-cursor";document.body.appendChild(cursor);document.addEventListener("mousemove",function(e){cursor.style.left=e.clientX+"px";cursor.style.top=e.clientY+"px"});const interactiveElements=document.querySelectorAll("a, button, input, select, textarea");interactiveElements.forEach(element=>{element.addEventListener("mouseenter",()=>{cursor.classList.add("active")});element.addEventListener("mouseleave",()=>{cursor.classList.remove("active")});element.addEventListener("mousedown",()=>{cursor.style.transform="translate(-50%, -50%) scale(0.8)"});element.addEventListener("mouseup",()=>{cursor.style.transform="translate(-50%, -50%) scale(1)"})});const cards=document.querySelectorAll(".card");cards.forEach(card=>{card.addEventListener("mouseenter",()=>{cursor.style.mixBlendMode="difference";cursor.style.border="2px solid white"});card.addEventListener("mouseleave",()=>{cursor.style.mixBlendMode="normal";cursor.style.border="2px solid var(--primary)"})});const images=document.querySelectorAll("img");images.forEach(image=>{image.addEventListener("mouseenter",()=>{cursor.style.borderRadius="0";cursor.style.width="40px";cursor.style.height="40px";cursor.style.border="2px solid white";cursor.style.mixBlendMode="difference"});image.addEventListener("mouseleave",()=>{cursor.style.borderRadius="50%";cursor.style.width="20px";cursor.style.height="20px";cursor.style.border="2px solid var(--primary)";cursor.style.mixBlendMode="normal"})});const textElements=document.querySelectorAll("p, h1, h2, h3, h4, h5, h6, span");textElements.forEach(element=>{element.addEventListener("mouseenter",()=>{cursor.style.width="5px";cursor.style.height="25px";cursor.style.borderRadius="2px"});element.addEventListener("mouseleave",()=>{cursor.style.width="20px";cursor.style.height="20px";cursor.style.borderRadius="50%"})});const navLinks=document.querySelectorAll(".nav-link");navLinks.forEach(link=>{link.addEventListener("mouseenter",()=>{cursor.style.backgroundColor="var(--primary)";cursor.style.mixBlendMode="multiply"});link.addEventListener("mouseleave",()=>{cursor.style.backgroundColor="transparent";cursor.style.mixBlendMode="normal"})});const buttons=document.querySelectorAll(".btn");buttons.forEach(button=>{button.addEventListener("mouseenter",()=>{cursor.style.transform="translate(-50%, -50%) scale(1.5)";cursor.style.backgroundColor="var(--primary)";cursor.style.opacity="0.5"});button.addEventListener("mouseleave",()=>{cursor.style.transform="translate(-50%, -50%) scale(1)";cursor.style.backgroundColor="transparent";cursor.style.opacity="1"})});const formFields=document.querySelectorAll("input, textarea, select");formFields.forEach(field=>{field.addEventListener("mouseenter",()=>{cursor.style.transform="translate(-50%, -50%) scale(0.5)";cursor.style.border="2px solid var(--primary)"});field.addEventListener("mouseleave",()=>{cursor.style.transform="translate(-50%, -50%) scale(1)";cursor.style.border="2px solid var(--primary)"});field.addEventListener("focus",()=>{cursor.style.display="none"});field.addEventListener("blur",()=>{cursor.style.display="block"})});document.body.style.cursor="none";document.addEventListener("mouseout",()=>{cursor.style.display="none"});document.addEventListener("mouseover",()=>{cursor.style.display="block"});let scrollTimeout;window.addEventListener("scroll",()=>{cursor.style.display="none";clearTimeout(scrollTimeout);scrollTimeout=setTimeout(()=>{cursor.style.display="block"},150)});document.addEventListener("visibilitychange",()=>{if(document.hidden){cursor.style.display="none"}else{cursor.style.display="block"}})});