document.addEventListener("DOMContentLoaded",function(){if(typeof AOS!=="undefined"){AOS.init({duration:600,easing:"ease-in-out",once:true,mirror:false})}if($.fn.counterUp){$(".counter").counterUp({delay:10,time:1e3})}const backToTopButton=document.getElementById("backToTop");if(backToTopButton){window.addEventListener("scroll",function(){if(window.pageYOffset>300){backToTopButton.style.display="block"}else{backToTopButton.style.display="none"}});backToTopButton.addEventListener("click",function(e){e.preventDefault();window.scrollTo({top:0,behavior:"smooth"})})}const navbar=document.querySelector(".navbar");if(navbar){window.addEventListener("scroll",function(){if(window.pageYOffset>50){navbar.classList.add("shadow-sm")}else{navbar.classList.remove("shadow-sm")}})}fetchStatistics();fetchLatestOffers()});function fetchStatistics(){console.log("Fetching statistics from backend...")}function fetchLatestOffers(){console.log("Fetching latest offers from backend...")}function createOfferCard(offer){const card=document.createElement("div");card.className="col-md-4";card.setAttribute("data-aos","fade-up");card.innerHTML=`\n        <div class="card border-0 shadow-sm h-100">\n            <div class="card-body p-4">\n                <span class="badge bg-primary mb-2">${offer.categorie}</span>\n                <h4 class="card-title">${offer.titre}</h4>\n                <p class="company-name text-muted"><i class="fas fa-building me-2"></i>${offer.entreprise}</p>\n                <p class="location text-muted"><i class="fas fa-map-marker-alt me-2"></i>${offer.lieu}</p>\n                <p class="duration text-muted"><i class="fas fa-calendar-alt me-2"></i>${offer.duree}</p>\n                <hr>\n                <div class="d-flex justify-content-between align-items-center">\n                    <span class="text-muted">Publié il y a ${offer.publie_il_y_a}</span>\n                    <a href="/offres/${offer.id}" class="btn btn-sm btn-outline-primary">Voir détails</a>\n                </div>\n            </div>\n        </div>\n    `;return card}