document.addEventListener("DOMContentLoaded",()=>{const savedTheme=localStorage.getItem("theme")||"light";document.body.setAttribute("data-theme",savedTheme);updateThemeIcon(savedTheme);const themeToggle=document.getElementById("theme-toggle");if(themeToggle){themeToggle.addEventListener("click",()=>{const currentTheme=document.body.getAttribute("data-theme");const newTheme=currentTheme==="light"?"dark":"light";document.body.setAttribute("data-theme",newTheme);localStorage.setItem("theme",newTheme);updateThemeIcon(newTheme)})}const cursor=document.createElement("div");cursor.classList.add("custom-cursor");document.body.appendChild(cursor);document.addEventListener("mousemove",e=>{cursor.style.left=e.clientX+"px";cursor.style.top=e.clientY+"px"});document.querySelectorAll("a, button, input, select, .clickable").forEach(element=>{element.addEventListener("mouseenter",()=>{cursor.classList.add("hover")});element.addEventListener("mouseleave",()=>{cursor.classList.remove("hover")})})});function updateThemeIcon(theme){const themeIcon=document.getElementById("theme-icon");if(themeIcon){themeIcon.className=theme==="light"?"fas fa-moon theme-switcher-icon":"fas fa-sun theme-switcher-icon"}}document.addEventListener("DOMContentLoaded",()=>{const animateOnScroll=()=>{const elements=document.querySelectorAll(".animate-on-scroll:not(.animated)");elements.forEach(element=>{const elementTop=element.getBoundingClientRect().top;const elementBottom=element.getBoundingClientRect().bottom;if(elementTop<window.innerHeight&&elementBottom>0){element.classList.add("animated");const animation=element.getAttribute("data-animation")||"fade-in";element.classList.add(animation)}})};window.addEventListener("scroll",animateOnScroll);animateOnScroll()});